import{_ as t,c as r,o as e,d as a}from"./app.c522a08b.js";const u=JSON.parse('{"title":"Skelettunders\xF6kningar","description":"","frontmatter":{},"headers":[{"level":2,"title":"Fr\xE5gest\xE4llningar","slug":"fragestallningar"}],"relativePath":"skelett.md","lastUpdated":1668539668000}'),d={name:"skelett.md"},s=a('<h1 id="skelettundersokningar" tabindex="-1">Skelettunders\xF6kningar <a class="header-anchor" href="#skelettundersokningar" aria-hidden="true">#</a></h1><p>Sl\xE4tr\xF6ntgen \xE4r f\xF6rstahandsval vid de allra flesta skelettunders\xF6kningar. Undantag g\xE4ller unders\xF6kning av ryggrad (DT halsrygg/br\xF6strygg/l\xE4ndrygg) d\xE4r DT i st\xE4llet \xE4r f\xF6rstahandsval.</p><h2 id="fragestallningar" tabindex="-1">Fr\xE5gest\xE4llningar <a class="header-anchor" href="#fragestallningar" aria-hidden="true">#</a></h2><table><thead><tr><th>Fr\xE5gest\xE4llning</th><th>Kommentar</th></tr></thead><tbody><tr><td><u>Generella: </u></td><td></td></tr><tr><td><b>Skelettskada?</b></td><td>- H\xF6ftfraktur - RTG, eller MR vid misst\xE4nkt inkilad h\xF6ftfraktur som inte syns p\xE5 RTG.<br> - B\xE4ckenfraktur - RTG eller DT om fraktur inte syns p\xE5 RTG och kvarst\xE5ende klinisk misstanke.<br> - Kotfraktur - DT<br> - Handledsfraktur - RTG<br> - Fotledsfraktur - RTG<br> - Axelfraktur - RTG<br> - Scaphoideumfraktur - RTG eller MR vid negativ RTG.</td></tr><tr><td><b>Felst\xE4llning?</b></td><td></td></tr><tr><td><b>Luxation?</b></td><td>- Axelledsluxation -RTG<br> - H\xF6ftprotesluxation -RTG</td></tr><tr><td><b>Artros?</b></td><td>RTG. Kr\xE4ver belastade bilder, dvs. att leden belastas vid bildtagning. Utf\xF6rs inte akut.</td></tr><tr><td><b>Protesl\xE4ge?</b></td><td>RTG.</td></tr><tr><td><b>Osteit?</b></td><td>Oftast RTG. DT och MR har dock h\xF6gre sensitivitet.</td></tr><tr><td><b>Skelettmetastaser?</b></td><td>Skelettscintigrafi \xE4r f\xF6rstahandsval vid isolerad fr\xE5gest\xE4llning kring skelettmetastaser. Ofta syns dock skelettmetastaser p\xE5 DT i samband med \xF6vrig metastasutredning, t.ex. DT-thorax-buk.</td></tr><tr><td></td><td></td></tr><tr><td><u>Specifika: </u></td><td></td></tr><tr><td><b>Kotkompression?</b></td><td>DT halsrygg/br\xF6strygg/l\xE4ndrygg. Vid ryggv\xE4rk hos sk\xF6ra \xE4ldre med falltrauma.</td></tr><tr><td><b>Prevertebral mjukdelssvullnad?</b></td><td>DT halsrygg. Vid misstanke om ryggskada/bl\xF6dning i samband med halsryggstrauma.</td></tr><tr><td><b>Inkilad h\xF6ftfraktur?</b></td><td>MR h\xF6ft. N\xE4r RTG h\xF6ft \xE4r normal men klinisk misstanke om h\xF6ftfraktur kvarst\xE5r.</td></tr><tr><td><b>Abscess?</b></td><td>DT b\xE4cken. Vid misstanke om abscess i t.ex. lilla b\xE4ckenet.</td></tr></tbody></table>',4),n=[s];function l(i,k,g,o,b,h){return e(),r("div",null,n)}const m=t(d,[["render",l]]);export{u as __pageData,m as default};
